from collections import deque

n = int(input())
dist = [[-1] * n for _ in range(n)]
dr = [1, 1, -1, -1, 2, 2, -2, -2]
dc = [2, -2, 2, -2, 1, -1, 1, -1]
q = deque([(0, 0, 0)])

while q:
    d, r, c = q.popleft()
    if dist[r][c] != -1:
        continue
    dist[r][c] = d
    for i in range(len(dr)):
        r1, c1 = r + dr[i], c + dc[i]
        if min(r1, c1) < 0 or max(r1, c1) >= n:
            continue
        q.append((d + 1, r1, c1))

for i in range(n):
    for j in range(n):
        print(dist[i][j], end=" ")
    print()

