def main():
    import sys
    from collections import Counter

    input = sys.stdin.read
    str_input = input().strip()
    n = len(str_input)
    freq = Counter(str_input)

    def getmax():
        return max(freq.values())

    v = getmax()
    left = n
    if v > (left + 1) // 2:
        print("-1")
        return

    pre = -1
    result = []
    for _ in range(n):
        for j in range(26):
            if chr(j + ord('A')) == pre or freq[chr(j + ord('A'))] == 0:
                continue
            freq[chr(j + ord('A'))] -= 1
            if getmax() <= left // 2:
                result.append(chr(j + ord('A')))
                left -= 1
                pre = chr(j + ord('A'))
                break
            freq[chr(j + ord('A'))] += 1

    print(''.join(result))

if __name__ == "__main__":
    main()

