from collections import defaultdict

def main():
    n = int(sys.stdin.readline().strip())
    heights = list(map(int, sys.stdin.readline().strip().split()))
    map = defaultdict(int)

    for c in heights:
        h = next((key for key in sorted(map.keys()) if key > c), None)
        if h is not None:
            if map[h] == 1:
                del map[h]
            else:
                map[h] -= 1
        map[c] += 1

    cnt = sum(map.values())
    print(cnt)

