class Book:
    def __init__(self, pages, price):
        self.pages = pages
        self.price = price

    def __lt__(self, other):
        return self.pages < other.pages

def main():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    num_books, max_cost = map(int, data[0].split())
    prices = list(map(int, data[1].split()))
    pages = list(map(int, data[2].split()))

    books = [Book(pages[i], prices[i]) for i in range(num_books)]
    books.sort()

    pages_per_price = [0] * (max_cost + 1)

    for book in books:
        price = book.price
        page = book.pages
        for j in range(max_cost, price - 1, -1):
            if pages_per_price[j] < page + pages_per_price[j - price]:
                pages_per_price[j] = page + pages_per_price[j - price]

    print(pages_per_price[max_cost])

if __name__ == "__main__":
    main()

