def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    str1, str2 = data[0], data[1]
    n, m = len(str1) + 1, len(str2) + 1
    BLANK = -1
    dp = [[BLANK] * m for _ in range(n)]
    dp[0][0] = 0

    def solve(i, j):
        if dp[i][j] != BLANK:
            return dp[i][j]
        dp[i][j] = float('inf')
        if i > 0 and j > 0:
            dp[i][j] = min(dp[i][j], (str1[i - 1] != str2[j - 1]) + solve(i - 1, j - 1))
        if i > 0:
            dp[i][j] = min(dp[i][j], 1 + solve(i - 1, j))
        if j > 0:
            dp[i][j] = min(dp[i][j], 1 + solve(i, j - 1))
        return dp[i][j]

    print(solve(n - 1, m - 1))

if __name__ == "__main__":
    main()

