def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    a = int(data[0])
    b = int(data[1])

    BLANK = -1
    dp = [[BLANK] * (b + 1) for _ in range(a + 1)]

    for i in range(min(a, b) + 1):
        dp[i][i] = 0

    def solve(i, j):
        if dp[i][j] != BLANK:
            return dp[i][j]
        dp[i][j] = float('inf')
        for k in range(1, i):
            dp[i][j] = min(dp[i][j], 1 + solve(k, j) + solve(i - k, j))
        for k in range(1, j):
            dp[i][j] = min(dp[i][j], 1 + solve(i, k) + solve(i, j - k))
        return dp[i][j]

    print(solve(a, b))

if __name__ == "__main__":
