def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    a = list(map(int, data[1:n+1]))

    adj = [[] for _ in range(n)]

    for it in range(2):
        st = [(int(1e9 + 1), -1)]
        for i in range(n):
            while st[-1][0] <= a[i]:
                st.pop()
            if len(st) > 1:
                adj[it * (n - 1 - i) if it else i].append(it * (n - 1 - st[-1][1]) if it else st[-1][1])
            st.append((a[i], i))
        a.reverse()

    dp = [-1] * n

    def go(u):
        if dp[u] != -1:
            return dp[u]
        dp[u] = 1
        for v in adj[u]:
            dp[u] = max(dp[u], 1 + go(v))
        return dp[u]

    ans = 0
    for i in range(n):
        ans = max(ans, go(i))

    print(ans)

if __name__ == "__main__":
    main()

