def bt(mask, mask2, idx):
    if idx == n:
        trans[mask].append(mask2)
        return
    if mask & (1 << idx):
        bt(mask, mask2, idx + 1)
    else:
        bt(mask, mask2 | (1 << idx), idx + 1)
        if idx + 1 < n and (mask & (1 << (idx + 1))) == 0:
            bt(mask, mask2, idx + 2)

