for i in range(m):
    for j in range(n2):
        if dp[i][j] == 0:
            continue
        for j2 in trans[j]:
            dp[i + 1][j2] += dp[i][j]
            if dp[i + 1][j2] >= MOD:
                dp[i + 1][j2] -= MOD

print(dp[m][0])

