from sys import stdin, stdout

BLANK = -1
dp = []
str_digits = []

def solve(dig, curdigit, matched, leadingzeros):
    if dp[dig][curdigit][matched][leadingzeros] != BLANK:
        return dp[dig][curdigit][matched][leadingzeros]
    if dig == 0:
        dp[dig][curdigit][matched][leadingzeros] = not matched or curdigit <= str_digits[dig]
        return dp[dig][curdigit][matched][leadingzeros]
    LIM = str_digits[dig - 1] if matched else 9
    dp[dig][curdigit][matched][leadingzeros] = 0
    for i in range(LIM + 1):
        if i == curdigit and not leadingzeros:
            continue
        newmatched = matched
        newleadingzeros = leadingzeros
        if leadingzeros and i != 0:
            newleadingzeros = False
        if newmatched and i < str_digits[dig - 1]:
            newmatched = False
        dp[dig][curdigit][matched][leadingzeros] += solve(dig - 1, i, newmatched, newleadingzeros)
    return dp[dig][curdigit][matched][leadingzeros]

def calc(v):
    if v < 0:
        return 0
    if v == 0:
        return 1
    global str_digits, dp
    str_digits.clear()
    while v > 0:
        str_digits.append(v % 10)
        v //= 10
    dp = [[[ [BLANK for _ in range(2)] for _ in range(2)] for _ in range(10)] for _ in range(len(str_digits))]
    ans = 0
    for i in range(1 + str_digits[-1]):
        val = solve(len(str_digits) - 1, i, i == str_digits[-1], i == 0)
        ans += val
    return ans

def main():
    a, b = map(int, stdin.readline().split())
    b1 = calc(b)
    a1 = calc(a - 1)
    stdout.write(f"{b1 - a1}\n")

if __name__ == "__main__":
    main()

