import heapq

MOD = int(1e9 + 7)

def main():
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    m = int(data[1])

    wadj = [[] for _ in range(n)]
    adj = [[] for _ in range(n)]

    index = 2
    for _ in range(m):
        a = int(data[index]) - 1
        b = int(data[index + 1]) - 1
        w = int(data[index + 2])
        index += 3
        wadj[a].append((b, w))

    dist = [float('inf')] * n
    dist[0] = 0
    visited = [False] * n
    pq = [(0, 0)]

    while pq:
        d, u = heapq.heappop(pq)
        if visited[u]:
            continue
        visited[u] = True
        for v, weight in wadj[u]:
            if dist[v] > dist[u] + weight:
                dist[v] = dist[u] + weight
                heapq.heappush(pq, (dist[v], v))

    for i in range(n):
        for p in wadj[i]:
            if dist[i] + p[1] == dist[p[0]]:
                adj[i].append(p[0])

    print(dist[n - 1], end=" ")

    BLANK = -1
    ways = [BLANK] * n

    def solve(cur):
        if ways[cur] != BLANK:
            return ways[cur]
        if cur == n - 1:
            return (ways[cur] := 1)
        ways[cur] = 0
        for i in adj[cur]:
            ways[cur] = (ways[cur] + solve(i)) % MOD
        return ways[cur]

    print(solve(0), end=" ")

    dist1 = [BLANK] * n

    def solve3(cur):
        if dist1[cur] != BLANK:
            return dist1[cur]
        if cur == n - 1:
            dist1[cur] = 0
            return dist1[cur]
        dist1[cur] = int(1e8)
        for i in adj[cur]:
            dist1[cur] = min(dist1[cur], 1 + solve3(i))
        return dist1[cur]

    solve3(0)
    print(dist1[0], end=" ")

    BLANK = float('-inf')
    low = -int(1e8)
    dist1 = [BLANK] * n

    def solve2(cur):
        if dist1[cur] != BLANK:
            return dist1[cur]
        if cur == n - 1:
            dist1[cur] = 0
            return dist1[cur]
        dist1[cur] = 0
        for i in adj[cur]:
            dist1[cur] = max(dist1[cur], 1 + solve2(i))
        if dist1[cur] == 0 and cur != n - 1:
            dist1[cur] = low
        return dist1[cur]

    solve2(0)
    print(dist1[0])

