class TwoSat:
    def __init__(self, n=0):
        self.N = n
        self.gr = [[] for _ in range(2 * n)]
        self.values = [-1] * n

    def add_var(self):
        self.gr.append([])
        self.gr.append([])
        self.N += 1
        return self.N - 1

    def either(self, f, j):
        f = max(2 * f, -1 - 2 * f)
        j = max(2 * j, -1 - 2 * j)
        self.gr[f].append(j ^ 1)
        self.gr[j].append(f ^ 1)

    def set_value(self, x):
        self.either(x, x)

    def at_most_one(self, li):
        if len(li) <= 1:
            return
        cur = ~li[0]
        for i in range(2, len(li)):
            next_var = self.add_var()
            self.either(cur, ~li[i])
            self.either(cur, next_var)
            self.either(~li[i], next_var)
            cur = ~next_var
        self.either(cur, ~li[1])

    def dfs(self, i):
        low = self.val[i] = self.time + 1
        self.time += 1
        self.z.append(i)
        for e in self.gr[i]:
            if not self.comp[e]:
                low = min(low, self.val[e] if self.val[e] else self.dfs(e))
        if low == self.val[i]:
            while True:
                x = self.z.pop()
                self.comp[x] = low
                if self.values[x >> 1] == -1:
                    self.values[x >> 1] = x & 1
                if x == i:
                    break
        return self.val[i] = low

    def solve(self):
        self.values = [-1] * self.N
        self.val = [0] * (2 * self.N)
        self.comp = self.val[:]
        self.z = []
        self.time = 0
        for i in range(2 * self.N):
            if not self.comp[i]:
                self.dfs(i)
        for i in range(self.N):
            if self.comp[2 * i] == self.comp[2 * i + 1]:
                return False
        return True


def main():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    n, m = map(int, data[0].split())
    ts = TwoSat(m)

    for i in range(1, n + 1):
        s1, v1, s2, v2 = data[i].split()
        v1, v2 = int(v1) - 1, int(v2) - 1
        ts.either(v1 if s1 == '+' else ~v1, v2 if s2 == '+' else ~v2)

    works = ts.solve()
    if works:
        print(" ".join('+' if ts.values[i] else '-' for i in range(m)))
    else:
        print("IMPOSSIBLE")


if __name__ == "__main__":
    main()

