from collections import deque

class Node:
    def __init__(self, id):
        self.parent = None
        self.id = id
        self.visited = False
        self.neighbors = set()
        self.weights = {}

def bfs(start, end, nodes):
    q = deque()
    for n in nodes:
        n.parent = None
        n.visited = False
    q.append(start)
    while q:
        n = q.popleft()
        if n.visited:
            continue
        n.visited = True
        if n == end:
            return True
        for n2 in n.neighbors:
            if not n2.visited and n.weights[n2] > 0:
                n2.parent = n
                q.append(n2)
    return False

def main():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    n, m = map(int, data[0].split())
    nodes = [Node(i) for i in range(n)]

    for i in range(1, m + 1):
        a, b, c = map(int, data[i].split())
        a -= 1
        b -= 1
        if nodes[b] not in nodes[a].neighbors:
            nodes[a].neighbors.add(nodes[b])
            nodes[a].weights[nodes[b]] = c
        else:
            nodes[a].weights[nodes[b]] += c
        if nodes[a] not in nodes[b].neighbors:
            nodes[b].neighbors.add(nodes[a])
            nodes[b].weights[nodes[a]] = 0

    max_flow = 0
    while bfs(nodes[0], nodes[n - 1], nodes):
        min_flow = float('inf')
        v = nodes[n - 1]
        while v.parent is not None:
            min_flow = min(min_flow, v.parent.weights[v])
            v = v.parent
        max_flow += min_flow
        v = nodes[n - 1]
        while v.parent is not None:
            v.weights[v.parent] += min_flow
            v.parent.weights[v] -= min_flow
            v = v.parent

    print(max_flow)

