from collections import defaultdict, deque
import sys

def edmonds_karp(graph, source, sink):
    assert source != sink
    flow = 0
    par = [-1] * len(graph)

    while True:
        fill(par, -1)
        par[source] = 0
        ptr = 1
        q = [source]

        while ptr > 0:
            x = q.pop(0)
            for e in graph[x].items():
                if par[e[0]] == -1 and e[1] > 0:
                    par[e[0]] = x
                    q.append(e[0])
                    ptr += 1
                    if e[0] == sink:
                        break
            else:
                continue
            break
        else:
            return flow

        inc = float('inf')
        y = sink
        while y != source:
            inc = min(inc, graph[par[y]][y])
            y = par[y]

        flow += inc
        y = sink
        while y != source:
            p = par[y]
            if (graph[p][y] := graph[p][y] - inc) <= 0:
                del graph[p][y]
            graph[y][p] += inc
            y = par[y]

def routes(graph, graph1, source, sink):
    par = [-1] * len(graph)
    rs = []

    while True:
        fill(par, -1)
        par[source] = source
        ptr = 1
        q = [source]

        while ptr > 0:
            x = q.pop(0)
            for e in graph[x].items():
                if par[e[0]] == -1 and e[1] < graph1[x][e[0]]:
                    par[e[0]] = x
                    q.append(e[0])
                    ptr += 1
                    if e[0] == sink:
                        break
            else:
                continue
            break
        else:
            for r in rs:
                r.reverse()
            return rs

        rs.append([])
        y = sink
        while y != source:
            graph[par[y]][y] += 1
            graph[y][par[y]] -= 1
            rs[-1].append(y)
            y = par[y]
        rs[-1].append(source)

