import sys

def index_max(tree, t):
    size = len(tree) // 2
    start = 1
    if tree[start] < t:
        return -1
    while 2 * start < len(tree):
        if tree[2 * start] >= t:
            start = 2 * start
        elif tree[2 * start + 1] >= t:
            start = 2 * start + 1
        else:
            start = -1
            break
    if start >= size:
        update(tree, start - size, tree[start] - t)
        return start - size
    return start

def maximum(tree, i1, i2):
    size = len(tree) // 2
    i1 += size
    i2 += size
    max_val = float('-inf')
    while i1 <= i2:
        if i1 % 2 == 1:
            max_val = max(max_val, tree[i1])
            i1 += 1
        if i2 % 2 == 0:
            max_val = max(max_val, tree[i2])
            i2 -= 1
        i1 //= 2
        i2 //= 2
    return max_val

def update(tree, index, value):
    size = len(tree) // 2
    index += size
    tree[index] = value
    for index //= 2; index >= 1; index //= 2:
        tree[index] = max(tree[2 * index], tree[2 * index + 1])

def main():
    input = sys.stdin.read
    data = input().splitlines()
    size, num_queries = map(int, data[0].split())
    size2 = size
    while (size2 & -size2) != size2:
        size2 += size2 & -size2
    arr = [0] * size
    tree = [float('-inf')] * (2 * size2)

    arr = list(map(int, data[1].split()))
    for i in range(size):
        update(tree, i, arr[i])

    queries = list(map(int, data[2].split()))
    result = []
    for i in range(num_queries):
        t = queries[i]
        index = index_max(tree, t)
        result.append(index + 1)

    print(" ".join(map(str, result)))

