class Node:
    def __init__(self, lo, hi, v=None):
        self.l = None
        self.r = None
        self.lo = lo
        self.hi = hi
        self.mset = float('inf')
        self.madd = 0
        self.val = -float('inf')
        if v is not None:
            if lo + 1 < hi:
                mid = lo + (hi - lo) // 2
                self.l = Node(lo, mid, v)
                self.r = Node(mid, hi, v)
                self.val = max(self.l.val, self.r.val)
            else:
                self.val = v[lo]

    def query(self, L, R):
        if R <= self.lo or self.hi <= L:
            return -float('inf')
        if L <= self.lo and self.hi <= R:
            return self.val
        self.push()
        return max(self.l.query(L, R), self.r.query(L, R))

    def set(self, L, R, x):
        if R <= self.lo or self.hi <= L:
            return
        if L <= self.lo and self.hi <= R:
            self.mset = self.val = x
            self.madd = 0
        else:
            self.push()
            self.l.set(L, R, x)
            self.r.set(L, R, x)
            self.val = max(self.l.val, self.r.val)

    def add(self, L, R, x):
        if R <= self.lo or self.hi <= L:
            return
        if L <= self.lo and self.hi <= R:
            if self.mset != float('inf'):
                self.mset += x
            else:
                self.madd += x
            self.val += x
        else:
            self.push()
            self.l.add(L, R, x)
            self.r.add(L, R, x)
            self.val = max(self.l.val, self.r.val)

    def push(self):
        if not self.l:
            mid = self.lo + (self.hi - self.lo) // 2
            self.l = Node(self.lo, mid)
            self.r = Node(mid, self.hi)
        if self.mset != float('inf'):
            self.l.set(self.lo, self.hi, self.mset)
            self.r.set(self.lo, self.hi, self.mset)
            self.mset = float('inf')
        elif self.madd:
            self.l.add(self.lo, self.hi, self.madd)
            self.r.add(self.lo, self.hi, self.madd)
            self.madd = 0

