    import sys
    from collections import defaultdict

    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    adj = defaultdict(list)

    index = 1
    for _ in range(n - 1):
        a = int(data[index]) - 1
        b = int(data[index + 1]) - 1
        adj[a].append(b)
        adj[b].append(a)
        index += 2

    BLANK = -1
    dp = [[BLANK] * 2 for _ in range(n)]

    def dfs(cur, pre, used):
        if dp[cur][used] != BLANK:
            return dp[cur][used]
        dp[cur][used] = 0
        reg = 0
        for i in adj[cur]:
            if i == pre:
                continue
            reg += max(dfs(i, cur, 0), dfs(i, cur, 1))
        if used:
            for i in adj[cur]:
                if i == pre:
                    continue
                dp[cur][used] = max(dp[cur][used], 1 + reg - max(dfs(i, cur, 0), dfs(i, cur, 1)) + dfs(i, cur, 0))
        else:
            dp[cur][used] = reg
        return dp[cur][used]

    print(max(dfs(0, -1, 0), dfs(0, -1, 1)))

