from math import gcd

MOD = int(1e9 + 7)
MOD2 = MOD - 1

def modmul(a, b, M):
    ret = a * b - M * int(1.0 / M * a * b)
    return ret + M * (ret < 0) - M * (ret >= M)

def modpow(b, e, mod):
    ans = 1
    while e:
        b = modmul(b, b, mod)
        if e & 1:
            ans = modmul(ans, b, mod)
        e //= 2
    return ans

def is_prime(n):
    if n < 2 or n % 6 % 4 != 1:
        return (n | 1) == 3
    A = [2, 325, 9375, 28178, 450775, 9780504, 1795265022]
    s = (n - 1).bit_length() - 1
    d = n >> s
    for a in A:
        p = modpow(a % n, d, n)
        i = s
        while p != 1 and p != n - 1 and a % n and i:
            p = modmul(p, p, n)
            i -= 1
        if p != n - 1 and i != s:
            return False
    return True

def pollard(n):
    def f(x):
        return modmul(x, x, n) + 1
    x = y = 0
    t = 30
    prd = 2
    i = 1
    while t % 40 or gcd(prd, n) == 1:
        if x == y:
            x = i + 1
            y = f(x)
        q = modmul(prd, max(x, y) - min(x, y), n)
        if q:
            prd = q
        x = f(x)
        y = f(f(y))
        t += 1
    return gcd(prd, n)

def factor(n):
    if n == 1:
        return []
    if is_prime(n):
        return [n]
    x = pollard(n)
    l = factor(x)
    r = factor(n // x)
    return l + r

def modinverse(a, mod):
    return modpow(a, mod - 2, mod)

