    num = (modpow(p[0], p[1] + 1, MOD) - 1) % MOD
    denom = modinverse(p[0] - 1, MOD)
    sumdiv = (sumdiv * ((num * denom) % MOD)) % MOD
    numdiv = mod(numdiv * (p[1] + 1))
    prod = mod(prod * modpow(p[0], p[1], MOD))
    if sq != -1:
        if p[1] % 2 == 0:
