def manacher(s):
    n = len(s)
    p = [[0] * (n + 1), [0] * n]
    for z in range(2):
        l = r = 0
        for i in range(n):
            t = r - i + (1 if z else 0)
            if i < r:
                p[z][i] = min(t, p[z][l + t])
            L = i - p[z][i]
            R = i + p[z][i] - (1 if z else 0)
            while L >= 1 and R + 1 < n and s[L - 1] == s[R + 1]:
                p[z][i] += 1
                L -= 1
                R += 1
            if R > r:
                l, r = L, R
    return p

def main():
