    input = sys.stdin.read
    s = input().strip()
    m = manacher(s)
    n = len(s)
    length = [0] * (n + 1)

    for i in range(len(m[0])):
        length[i + m[0][i]] = max(length[i + m[0][i]], 2 * m[0][i])

    for i in range(len(m[1])):
        length[i + 1 + m[1][i]] = max(length[i + 1 + m[1][i]], 2 * m[1][i] + 1)

    for i in range(n - 1, 0, -1):
        length[i] = max(length[i], length[i + 1] - 2)

    print(" ".join(map(str, length[1:n + 1])))

