import sys
from time import time

MOD = 10**9 + 7

def mod(k):
    return k % MOD

pows = []
C = int((time() * 1e6) % MOD)

LIM = int(2e5 + 100)
pows.append(1)
for i in range(LIM):
    pows.append(mod(pows[-1] * C))

class Node:
    def __init__(self, v, lo, hi):
        self.l = None
        self.r = None
        self.lo = lo
        self.hi = hi
        self.mid = 0
        if lo + 1 < hi:
            self.mid = lo + (hi - lo) // 2
            self.l = Node(v, lo, self.mid)
            self.r = Node(v, self.mid, hi)
            self.val = self.comb(self.l.val, self.r.val)
        else:
            self.val = ((ord(v[lo]) - ord('a') + 1, ord(v[lo]) - ord('a') + 1), 1)

    def comb(self, a, b):
        return ((mod(a[0][0] + mod(pows[a[1]] * b[0][0])), mod(mod(a[0][1] * pows[b[1]]) + b[0][1])), a[1] + b[1])

    def change(self, idx, c):
        if self.lo + 1 == self.hi:
            self.val = ((ord(c) - ord('a') + 1, ord(c) - ord('a') + 1), 1)
            return
        if self.mid <= idx:
            self.r.change(idx, c)
        else:
            self.l.change(idx, c)
        self.val = self.comb(self.l.val, self.r.val)

    def query(self, L, R):
        if self.hi <= L or R <= self.lo:
            return ((0, 0), 0)
        if L <= self.lo and self.hi <= R:
            return self.val
        return self.comb(self.l.query(L, R), self.r.query(L, R))

def main():
    input = sys.stdin.read
    data = input().splitlines()
    n, q = map(int, data[0].split())
    str_input = data[1]
    tree = Node(str_input, 0, len(str_input))

    output = []
    for i in range(2, 2 + q):
        query = list(map(int, data[i].split()))
        if query[0] == 1:
            idx = query[1] - 1
            c = query[2]
            tree.change(idx, c)
        else:
            l, r = query[1] - 1, query[2] - 1
            res = tree.query(l, r + 1)
            output.append("YES" if res[0][0] == res[0][1] else "NO")

    print("\n".join(output))

if __name__ == "__main__":
    main()

