from collections import deque

class AhoCorasick:
    alpha = 26
    first = ord('a')

    class Node:
        def __init__(self, v):
            self.back = None
            self.next = [-1] * AhoCorasick.alpha
            self.start = -1
            self.end = -1
            self.nmatches = 0
            self.ends = []
            self.trav = False

    def __init__(self, pat):
        self.N = [AhoCorasick.Node(-1)]
        self.backp = []
        for j, s in enumerate(pat):
            self.insert(s, j)
        self.N[0].back = len(self.N)
        self.N.append(AhoCorasick.Node(0))

        q = deque([0])
        while q:
            n = q.popleft()
            prev = self.N[n].back
            for i in range(AhoCorasick.alpha):
                ed = self.N[n].next[i]
                y = self.N[prev].next[i]
                if ed == -1:
                    self.N[n].next[i] = y
                else:
                    self.N[ed].back = y
                    if self.N[ed].end == -1:
                        self.N[ed].end = self.backp[self.N[ed].start]
                    self.N[ed].nmatches += self.N[y].nmatches
                    q.append(ed)

    def insert(self, s, j):
        assert s
        n = 0
        for c in s:
            m = self.N[n].next[ord(c) - AhoCorasick.first]
            if m == -1:
                n = m = len(self.N)
                self.N.append(AhoCorasick.Node(-1))
            else:
                n = m
        if self.N[n].end == -1:
            self.N[n].start = j
        self.backp.append(self.N[n].end)
        self.N[n].end = j
        self.N[n].ends.append(j)
        self.N[n].nmatches += 1

    def find(self, word, v):
        n = 0
        res = [False] * v
        self.N[n].trav = True
        for c in word:
            n = self.N[n].next[ord(c) - AhoCorasick.first]
            self.N[n].trav = True

        visited = [False] * len(self.N)
        q = deque()
        for i in range(len(self.N) - 1, -1, -1):
            if self.N[i].trav:
                q.append(i)

        while q:
            k = q.popleft()
            if visited[k]:
                continue
            visited[k] = True
            for j in self.N[k].ends:
                res[j] = True
            if k > 0 and not visited[self.N[k].back]:
                q.append(self.N[k].back)

        return res

def main():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    str_input = data[0]
    q = int(data[1])
    pat = [data[i + 2] for i in range(q)]

    ac = AhoCorasick(pat)
    res = ac.find(str_input, q)

    for i in range(q):
        print("YES" if res[i] else "NO")

if __name__ == "__main__":
    main()

