def Z(S):
    z = [0] * len(S)
    l, r = -1, -1
    for i in range(1, len(S)):
        if i >= r:
            z[i] = 0
        else:
            z[i] = min(r - i, z[i - l])
        while i + z[i] < len(S) and S[i + z[i]] == S[z[i]]:
            z[i] += 1
        if i + z[i] > r:
            l, r = i, i + z[i]
    return z

def kmp(s):
    p = [0] * len(s)
    for i in range(1, len(s)):
        g = p[i - 1]
        while g and s[i] != s[g]:
            g = p[g - 1]
        p[i] = g + (s[i] == s[g])
    return p

