class Node:
    def __init__(self, lo, hi):
        self.l = None
        self.r = None
        self.lo = lo
        self.hi = hi
        self.madd = 0
        self.val = 0
        if lo + 1 < hi:
            mid = lo + (hi - lo) // 2
            self.l = Node(lo, mid)
            self.r = Node(mid, hi)
            self.comb()

    def combine(self, l, r):
        return l + r

    def comb(self):
        self.val = self.combine(self.l.val, self.r.val)

    def add(self, L, R, x):
        if R <= self.lo or self.hi <= L:
            return
        if L <= self.lo and self.hi <= R:
            self.madd += x
            self.val += x
        else:
            self.push()
            self.l.add(L, R, x)
            self.r.add(L, R, x)
            self.comb()

    def dfs(self):
        if self.lo + 1 == self.hi:
            print(self.val, end=" ")
        else:
            self.push()
            self.l.dfs()
            self.r.dfs()

    def push(self):
        if self.madd:
            self.l.add(self.lo, self.hi, self.madd)
            self.r.add(self.lo, self.hi, self.madd)
            self.madd = 0


