import sys
import math
from typing import Tuple

class Point:
    def __init__(self, x=0, y=0):
        self.x = x
        self.y = y

    def __lt__(self, p: 'Point') -> bool:
        return (self.x, self.y) < (p.x, p.y)

    def __eq__(self, p: 'Point') -> bool:
        return (self.x, self.y) == (p.x, p.y)

    def __add__(self, p: 'Point') -> 'Point':
        return Point(self.x + p.x, self.y + p.y)

    def __sub__(self, p: 'Point') -> 'Point':
        return Point(self.x - p.x, self.y - p.y)

    def __mul__(self, d: float) -> 'Point':
        return Point(self.x * d, self.y * d)

    def __truediv__(self, d: float) -> 'Point':
        return Point(self.x / d, self.y / d)

    def dot(self, p: 'Point') -> float:
        return self.x * p.x + self.y * p.y

    def cross(self, p: 'Point') -> float:
        return self.x * p.y - self.y * p.x

    def cross_two(self, a: 'Point', b: 'Point') -> float:
        return (a - self).cross(b - self)

    def dist2(self) -> float:
        return self.x * self.x + self.y * self.y

    def dist(self) -> float:
        return math.sqrt(self.dist2())

    def angle(self) -> float:
        return math.atan2(self.y, self.x)

    def unit(self) -> 'Point':
        return self / self.dist()

    def perp(self) -> 'Point':
        return Point(-self.y, self.x)

    def normal(self) -> 'Point':
        return self.perp().unit()

    def rotate(self, a: float) -> 'Point':
        return Point(self.x * math.cos(a) - self.y * math.sin(a), 
                     self.x * math.sin(a) + self.y * math.cos(a))

    def __repr__(self) -> str:
        return f"({self.x},{self.y})"

def main():
    input = sys.stdin.read
    data = input().splitlines()
    n = int(data[0])

    results = []
    for i in range(1, n + 1):
        p1, p2, p3 = Point(), Point(), Point()
        p1.x, p1.y, p2.x, p2.y, p3.x, p3.y = map(int, data[i].split())
        dif = p2 - p1
        sig = dif.cross(p3 - p1)

        if sig == 0:
            results.append("TOUCH")
        elif sig > 0:
            results.append("LEFT")
        else:
            results.append("RIGHT")

    print("\n".join(results))

