import sys
import math
from collections import namedtuple

Point = namedtuple('Point', ['x', 'y'])

def sgn(x):
    return (x > 0) - (x < 0)

def on_segment(s, e, p):
    return p.cross(s, e) == 0 and (s - p).dot(e - p) <= 0

def seg_inter(a, b, c, d):
    oa = c.cross(d, a)
    ob = c.cross(d, b)
    oc = a.cross(b, c)
    od = a.cross(b, d)

    if sgn(oa) * sgn(ob) < 0 and sgn(oc) * sgn(od) < 0:
        return [a * ob - b * oa / (ob - oa)]

    s = set()
    if on_segment(c, d, a): s.add(a)
    if on_segment(c, d, b): s.add(b)
    if on_segment(a, b, c): s.add(c)
    if on_segment(a, b, d): s.add(d)

    return list(s)

class P:
    def __init__(self, x=0, y=0):
        self.x = x
        self.y = y

    def __lt__(self, p):
        return (self.x, self.y) < (p.x, p.y)

    def __eq__(self, p):
        return (self.x, self.y) == (p.x, p.y)

    def __add__(self, p):
        return P(self.x + p.x, self.y + p.y)

    def __sub__(self, p):
        return P(self.x - p.x, self.y - p.y)

    def __mul__(self, d):
        return P(self.x * d, self.y * d)

    def __truediv__(self, d):
        return P(self.x / d, self.y / d)

    def dot(self, p):
        return self.x * p.x + self.y * p.y

    def cross(self, p):
        return self.x * p.y - self.y * p.x

    def cross_two(self, a, b):
        return (a - self).cross(b - self)

    def dist2(self):
        return self.x * self.x + self.y * self.y

    def dist(self):
        return math.sqrt(self.dist2())

    def angle(self):
        return math.atan2(self.y, self.x)

    def unit(self):
        return self / self.dist()

    def perp(self):
        return P(-self.y, self.x)

    def normal(self):
        return self.perp().unit()

    def rotate(self, a):
        return P(self.x * math.cos(a) - self.y * math.sin(a), self.x * math.sin(a) + self.y * math.cos(a))

    def __repr__(self):
