    l = (e - s).dist() * eps
    return (a > l) - (a < -l)

def on_segment(s, e, p):
    return p.cross(s, e) == 0 and (s - p).dot(e - p) <= 0

def in_polygon(p: List[Point], a: Point, strict=True):
