    def rotate(self, a):
        return Point(self.x * math.cos(a) - self.y * math.sin(a), 
                     self.x * math.sin(a) + self.y * math.cos(a))

def polygon_area(v: List[Point]) -> float:
    a = v[-1].cross(v[0])
    for i in range(len(v) - 1):
        a += v[i].cross(v[i + 1])
    return a

def cmp(a: Point, b: Point) -> bool:
    return a.y < b.y if a.y != b.y else a.x < b.x

def solve(l: int, r: int, cur: List[Point], byx: List[Point]) -> int:
    if len(cur) <= 3:
        best = int(9e18)
        for p1 in cur:
            for p2 in cur:
                if p1 != p2:
                    best = min(best, (p2 - p1).dist2())
        return best

    left, right, middle = [], [], []
    mid = (l + r) // 2
    x = byx[mid].x

    for p in cur:
        if p.x == x:
            middle.append(p)
        elif p.x < x:
            left.append(p)
        else:
            right.append(p)

    right.reverse()
    for p in middle:
        if len(left) * 2 < len(cur):
            left.append(p)
        else:
            right.append(p)

    right.reverse()
    d = min(solve(l, mid, left, byx), solve(mid + 1, r, right, byx))
    middle.clear()

    for p in cur:
        if (p.x - x) * (p.x - x) <= d:
            middle.append(p)

    for i in range(len(middle)):
        for j in range(max(0, i - 8), i):
            d = min(d, (middle[i] - middle[j]).dist2())

    return d

