import math
from collections import namedtuple

Point = namedtuple('Point', ['x', 'y'])

def sgn(x):
    return (x > 0) - (x < 0)

class P:
    def __init__(self, x=0, y=0):
        self.x = x
        self.y = y

    def __lt__(self, p):
        return (self.x, self.y) < (p.x, p.y)

    def __eq__(self, p):
        return (self.x, self.y) == (p.x, p.y)

    def __add__(self, p):
        return P(self.x + p.x, self.y + p.y)

    def __sub__(self, p):
        return P(self.x - p.x, self.y - p.y)

    def __mul__(self, d):
        return P(self.x * d, self.y * d)

    def __truediv__(self, d):
        return P(self.x / d, self.y / d)

    def dot(self, p):
        return self.x * p.x + self.y * p.y

    def cross(self, p):
        return self.x * p.y - self.y * p.x

    def cross2(self, a, b):
        return (a - self).cross(b - self)

    def dist2(self):
        return self.x * self.x + self.y * self.y

    def dist(self):
        return math.sqrt(self.dist2())

    def angle(self):
        return math.atan2(self.y, self.x)

    def unit(self):
        return self / self.dist()

    def perp(self):
        return P(-self.y, self.x)

    def normal(self):
        return self.perp().unit()

    def rotate(self, a):
        return P(self.x * math.cos(a) - self.y * math.sin(a), 
                 self.x * math.sin(a) + self.y * math.cos(a))

def on_segment(s, e, p):
    return p.cross(s, e) == 0 and (s - p).dot(e - p) <= 0

def side_of(s, e, p):
    return sgn(s.cross(e, p))

def side_of_eps(s, e, p, eps):
    a = (e - s).cross(p - s)
    l = (e - s).dist() * eps
    return (a > l) - (a < -l)

def convex_hull(pts):
    if len(pts) <= 1:
        return pts
    pts.sort()
    h = [None] * (len(pts) + 1)
    s = 0
    t = 0
    for _ in range(2):
        s = --t
        pts.reverse()
        for p in pts:
            while t >= s + 2 and h[t - 2].cross(h[t - 1], p) <= 0:
                t -= 1
            h[t] = p
            t += 1
    return h[:t - (t == 2 and h[0] == h[1])]

def in_hull(l, p, strict=True):
    a = 1
    b = len(l) - 1
    r = not strict
    if len(l) < 3:
        return r and on_segment(l[0], l[-1], p)
    if side_of(l[0], l[a], l[b]) > 0:
        a, b = b, a
    if side_of(l[0], l[a], p) >= r or side_of(l[0], l[b], p) <= -r:
        return False
    while abs(a - b) > 1:
        c = (a + b) // 2
        (b if side_of(l[0], l[c], p) > 0 else a) = c
    return sgn(l[a].cross(l[b], p)) < r

def main():
    input = sys.stdin.read
    data = input().splitlines()
    n = int(data[0])
    v = [P(*map(int, line.split())) for line in data[1:n + 1]]
    res = convex_hull(v)
    ans = []
    for i in range(n):
        if in_hull(res, v[i], False) and not in_hull(res, v[i], True):
            ans.append(v[i])
    print(len(ans))
    for p in ans:
        print(p.x, p.y)

