        self.val = self.combine(self.l.val, self.r.val)

    def query(self, L, R):
        if R <= self.lo or self.hi <= L:
            return 0
        if L <= self.lo and self.hi <= R:
            return self.val
        self.push()
        return self.combine(self.l.query(L, R), self.r.query(L, R))

    def add(self, L, R, x):
        if R <= self.lo or self.hi <= L:
            return
        if L <= self.lo and self.hi <= R:
            self.madd += x
            self.val += x
        else:
            self.push()
            self.l.add(L, R, x)
            self.r.add(L, R, x)
            self.comb()

    def push(self):
        if not self.l:
            mid = self.lo + (self.hi - self.lo) // 2
            self.l = Node(self.lo, mid)
