def each(n: Optional[Node], f):
    if n:
        each(n.l, f)
        f(n.val)
        each(n.r, f)

def split(n: Optional[Node], k: int) -> Tuple[Optional[Node], Optional[Node]]:
    if not n:
        return None, None
    if cnt(n.l) >= k:
        pa = split(n.l, k)
        n.l = pa[1]
        n.recalc()
        return pa[0], n
    else:
        pa = split(n.r, k - cnt(n.l) - 1)
        n.r = pa[0]
        n.recalc()
        return n, pa[1]

