    a, b = split(t, l)
    b, c = split(b, r - l)
    if k <= l:
        return merge(ins(a, b, k), c)
    else:
        return merge(a, ins(c, b, k - r))

def dfs(cur: Optional[Node], f):
    if cur is None:
        return
    dfs(cur.l, f)
    f(cur.val)
    dfs(cur.r, f)

