import sys
from collections import namedtuple
import math

input = sys.stdin.read
INT_MIN = -math.inf
INT_MAX = math.inf

dp = [[0] * 3001 for _ in range(3001)]
res = [[(0, 0)] * 3001 for _ in range(3001)]
pref = [0] * 3001
x = [0] * 3001

class DP:
    def __init__(self, l):
        self.layer = l

    def lo(self, ind):
        return 0

    def hi(self, ind):
        return 1 if self.layer == 1 else ind

    def f(self, ind, k):
        return dp[self.layer - 1][k] + (pref[ind] - pref[k]) ** 2

    def store(self, ind, k, v):
        res[self.layer][ind] = (k, v)
        dp[self.layer][ind] = v

    def rec(self, L, R, LO, HI):
        if L >= R:
            return
        mid = (L + R) // 2
        best = (float('inf'), LO)
        for k in range(max(LO, self.lo(mid)), min(HI, self.hi(mid))):
            best = min(best, (self.f(mid, k), k))
        self.store(mid, best[1], best[0])
        self.rec(L, mid, LO, best[1] + 1)
        self.rec(mid + 1, R, best[1], HI)

    def solve(self, L, R):
        self.rec(L, R, INT_MIN, INT_MAX)

def main():
    n, k = map(int, input().split())
    for i in range(1, n + 1):
        x[i] = int(input())
        pref[i] = x[i] + pref[i - 1]

    d = []
    for i in range(k + 1):
        d.append(DP(i))
        if i:
            d[-1].solve(1, n + 1)

    print(dp[k][n])

if __name__ == "__main__":
    main()

