import sys
import cmath
from collections import deque

def fft(a):
    n = len(a)
    L = 31 - (n - 1).bit_length()
    R = [1] * (2 * n)
    rt = [1] * (2 * n)

    for k in range(2, n * 2, k * 2):
        x = cmath.exp(complex(0, cmath.pi) / k)
        for i in range(k, 2 * k):
            rt[i] = R[i] = R[i // 2] * x if i & 1 else R[i // 2]

    rev = [0] * n
    for i in range(n):
        rev[i] = (rev[i // 2] | (i & 1) << L) // 2

    for i in range(n):
        if i < rev[i]:
            a[i], a[rev[i]] = a[rev[i]], a[i]

    for k in range(1, n, k * 2):
        for i in range(0, n, 2 * k):
            for j in range(k):
                z = rt[j + k] * a[i + j + k]
                a[i + j + k] = a[i + j] - z
                a[i + j] += z

def conv(a, b):
    if not a or not b:
        return []
    res = [0] * (len(a) + len(b) - 1)
    L = 32 - (len(res) - 1).bit_length()
    n = 1 << L
    in_data = [0] * n
    out_data = [0] * n

    for i in range(len(a)):
        in_data[i] = complex(a[i], 0)
    for i in range(len(b)):
        in_data[i].imag = b[i]

    fft(in_data)

    for i in range(n):
        in_data[i] *= in_data[i]

    for i in range(n):
        out_data[i] = in_data[-i & (n - 1)] - in_data[i].conjugate()

    fft(out_data)

    for i in range(len(res)):
        res[i] = out_data[i].imag / (4 * n)

    return res

def main():
    input = sys.stdin.read
    str_input = input().strip()
    v = [int(c) for c in str_input]
    v2 = list(reversed(v))
    res = conv(v, v2)
    res = res[:-1]
    res.reverse()
    print(" ".join(str(int(0.1 + x)) for x in res))

if __name__ == "__main__":
    main()

