class RollbackUF:
    def __init__(self, n):
        self.e = [-1] * n
        self.st = []
        self.numcomps = n

    def size(self, x):
        return -self.e[self.find(x)]

    def find(self, x):
        if self.e[x] < 0:
            return x
        return self.find(self.e[x])

    def time(self):
        return len(self.st)

    def rollback(self, t):
        for i in range(self.time() - 1, t - 1, -1):
            self.e[self.st[i][0]] = self.st[i][1]
            self.numcomps = self.st[i][2]
        self.st = self.st[:t]

    def join(self, a, b):
        a = self.find(a)
        b = self.find(b)
        if a == b:
            return False
        if self.e[a] > self.e[b]:
            a, b = b, a
        self.st.append((a, self.e[a], self.numcomps))
        self.st.append((b, self.e[b], self.numcomps))
        self.e[a] += self.e[b]
        self.e[b] = a
        self.numcomps -= 1
        return True


class Node:
    def __init__(self, lo, hi):
        self.l = None
        self.r = None
        self.lo = lo
        self.hi = hi
        self.edges = []
        if lo + 1 < hi:
            mid = lo + (hi - lo) // 2
            self.l = Node(lo, mid)
            self.r = Node(mid, hi)

    def add(self, L, R, edge):
        if R <= self.lo or self.hi <= L:
            return
        if L <= self.lo and self.hi <= R:
            self.edges.append(edge)
        else:
            self.l.add(L, R, edge)
            self.r.add(L, R, edge)

    def dfs(self, L, R, uf):
        if R <= self.lo or self.hi <= L:
            return
        t = uf.time()
        for p in self.edges:
            uf.join(p[0], p[1])
        if self.lo + 1 == self.hi:
            print(uf.numcomps, end=" ")
        else:
            self.l.dfs(L, R, uf)
            self.r.dfs(L, R, uf)
        uf.rollback(t)


def main():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    n, m, k = map(int, data[0].split())
    tree = Node(0, k + 50)
    edges = {}
    uf = RollbackUF(n)

    for i in range(1, m + 1):
        a, b = map(int, data[i].split())
        a -= 1
        b -= 1
        if a > b:
            a, b = b, a
        if (a, b) not in edges:
            edges[(a, b)] = []
        edges[(a, b)].append(0)

    for i in range(m + 1, m + k + 1):
        t, a, b = map(int, data[i].split())
        a -= 1
        b -= 1
        if a > b:
            a, b = b, a
        if (a, b) not in edges:
            edges[(a, b)] = []
        edges[(a, b)].append(i - m)

    addend = []
    for key, val in edges.items():
        if len(val) % 2 == 1:
            addend.append(key)

    for p in addend:
        edges[p].append(k + 5)

    for key, val in edges.items():
        assert len(val) % 2 == 0
        for i in range(0, len(val), 2):
            tree.add(val[i], val[i + 1], key)

    tree.dfs(0, k + 1, uf)
    print()

