import sys
from collections import defaultdict
import heapq

INF = float('inf')

class MCMF:
    def __init__(self, N):
        self.N = N
        self.ed = [[] for _ in range(N)]
        self.red = [[] for _ in range(N)]
        self.cap = [[0] * N for _ in range(N)]
        self.flow = [[0] * N for _ in range(N)]
        self.cost = [[0] * N for _ in range(N)]
        self.seen = [0] * N
        self.par = [None] * N
        self.dist = [INF] * N
        self.rel = [0] * N

    def addEdge(self, from_node, to_node, cap, cost):
        self.cap[from_node][to_node] = cap
        self.cost[from_node][to_node] = cost
        self.ed[from_node].append(to_node)
        self.red[to_node].append(from_node)

    def path(self, s):
        self.seen = [0] * self.N
        self.dist = [INF] * self.N
        self.dist[s] = 0
        di = 0

        q = []
        its = [None] * self.N
        heapq.heappush(q, (0, s))

        def relax(i, cap, cost, dir):
            nonlocal di
            val = di - self.rel[i] + cost
            if cap and val < self.dist[i]:
                self.dist[i] = val
                self.par[i] = (s, dir)
                if its[i] is None:
                    its[i] = heapq.heappush(q, (-self.dist[i], i))
                else:
                    heapq.heappush(q, (-self.dist[i], i))

        while q:
            s = heapq.heappop(q)[1]
            self.seen[s] = 1
            di = self.dist[s] + self.rel[s]
            for i in self.ed[s]:
                if not self.seen[i]:
                    relax(i, self.cap[s][i] - self.flow[s][i], self.cost[s][i], 1)
            for i in self.red[s]:
                if not self.seen[i]:
                    relax(i, self.flow[i][s], -self.cost[i][s], 0)

        for i in range(self.N):
            self.rel[i] = min(self.rel[i] + self.dist[i], INF)

    def maxflow(self, s, t):
        totflow = 0
        totcost = 0
        while True:
            self.path(s)
            if not self.seen[t]:
                break
            fl = INF
            for x in range(t, s, -1):
                p, r = self.par[x]
                fl = min(fl, self.cap[p][x] - self.flow[p][x] if r else self.flow[x][p])
            totflow += fl
            for x in range(t, s, -1):
                p, r = self.par[x]
                if r:
                    self.flow[p][x] += fl
                else:
                    self.flow[x][p] -= fl

        for i in range(self.N):
            for j in range(self.N):
                totcost += self.cost[i][j] * self.flow[i][j]
        return (totflow, totcost)

    def setrel(self, s):
        self.rel = [INF] * self.N
        self.rel[s] = 0
        it = self.N
        ch = 1
        while ch and it:
            ch = 0
            for i in range(self.N):
                if self.rel[i] != INF:
                    for to in self.ed[i]:
                        if self.cap[i][to]:
                            v = self.rel[i] + self.cost[i][to]
                            if v < self.rel[to]:
                                self.rel[to] = v
                                ch = 1
            it -= 1
        assert it >= 0  # negative cost cycle

def main():
    input = sys.stdin.read
    data = input().split()
    idx = 0
    n = int(data[idx])
    idx += 1
    mcmf = MCMF(2 * n + 2)
    source = 2 * n
    sink = 2 * n + 1
    for i in range(n):
        mcmf.addEdge(source, i, 1, 0)
        mcmf.addEdge(n + i, sink, 1, 0)
    for i in range(n):
        for j in range(n):
            v = int(data[idx])
            idx += 1
            mcmf.addEdge(i, n + j, 1, v)
    ans = mcmf.maxflow(source, sink)
    print(ans[1])
    for i in range(n):
        for j in range(n):
            if mcmf.flow[i][n + j]:
                print(i + 1, j + 1)

if __name__ == "__main__":
    main()

