class FT:
    def __init__(self, n):
        self.s = [0] * n

    def update(self, pos, dif):
        while pos < len(self.s):
            self.s[pos] += dif
            pos |= pos + 1

    def query(self, pos):
        res = 0
        while pos > 0:
            res += self.s[pos - 1]
            pos &= pos - 1
        return res

    def lower_bound(self, sum):
        if sum <= 0:
            return -1
        pos = 0
        for pw in (1 << i for i in range(25, -1, -1)):
            if pos + pw <= len(self.s) and self.s[pos + pw - 1] < sum:
                pos += pw
                sum -= self.s[pos - 1]
        return pos

def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    k = int(data[1])
    a = list(map(int, data[2:n + 2]))

    c = sorted(set(a))
    a = [c.index(x) for x in a]

    ft = FT(len(c))
    inv = 0
    result = []

    for i in range(n):
        inv += ft.query(len(c)) - ft.query(a[i] + 1)
        ft.update(a[i], 1)
        if i >= k:
            inv -= ft.query(a[i - k])
            ft.update(a[i - k], -1)
        if i >= k - 1:
            result.append(inv)

    print(" ".join(map(str, result)))

