from collections import deque
import sys
import math

MAXT = 530000
inf = 4 * 10**18

class Line:
    def __init__(self, A, B):
        self.A = A
        self.B = B

    def eval(self, x):
        return self.A * x + self.B

    def cross_after(self, x, T):
        if x.A == self.A:
            return inf
        up = x.B - self.B
        dn = self.A - x.A
        if dn < 0:
            dn *= -1
            up *= -1
        incep = (-(-up // dn) if up <= 0 else (up + dn - 1) // dn)
        return incep if incep > T else inf

class KST:
    def __init__(self):
        self.tree = [None] * MAXT
        self.melt = [0] * MAXT
        self.T = 0
        self.n = 0

    def pull(self, p):
        l = self.tree[2 * p].eval(self.T)
        r = self.tree[2 * p + 1].eval(self.T)
        self.tree[p] = (self.tree[2 * p] if l < r or (l == r and self.tree[2 * p].A < self.tree[2 * p + 1].A) else self.tree[2 * p + 1])
        self.melt[p] = min(self.melt[2 * p], self.melt[2 * p + 1], self.tree[2 * p].cross_after(self.tree[2 * p + 1], self.T))

    def init(self, s, e, p, l):
        if s == e:
            self.tree[p] = l[s]
            self.melt[p] = inf
            return
        m = (s + e) // 2
        self.init(s, m, 2 * p, l)
        self.init(m + 1, e, 2 * p + 1, l)
        self.pull(p)

    def update(self, pos, v, s, e, p=1):
        if s == e:
            self.tree[p] = v
            return
        m = (s + e) // 2
        if pos <= m:
            self.update(pos, v, s, m, 2 * p)
        else:
            self.update(pos, v, m + 1, e, 2 * p + 1)
        self.pull(p)

    def query(self, s, e, ps, pe, p=1):
        if e < ps or pe < s:
            return inf
        if s <= ps and pe <= e:
            return self.tree[p].eval(self.T)
        pm = (ps + pe) // 2
        return min(self.query(s, e, ps, pm, 2 * p), self.query(s, e, pm + 1, pe, 2 * p + 1))

    def heaten(self, s, e, p):
        if self.melt[p] > self.T:
            return
        m = (s + e) // 2
        self.heaten(s, m, 2 * p)
        self.heaten(m + 1, e, 2 * p + 1)
        self.pull(p)

    def init_kst(self, l, _T):
        self.n = len(l)
        self.T = _T
        self.init(0, self.n - 1, 1, l)

    def heaten_kst(self, _T):
        assert self.T <= _T
        self.T = _T
        self.heaten(0, self.n - 1, 1)

kst = KST()

def main():
    input = sys.stdin.read
    data = input().split()
    n = int(data[0])
    k = int(data[1])
    a = list(map(int, data[2:n + 2]))

    left = [-1] * n
    right = [-1] * n
    anss = []

    for it in range(2):
        emp = Line(0, inf)
        init = [emp] * n
        kst.init_kst(init, -1)
        q = deque()
        qstart = 0
        ans = []

        for i in range(n):
            while q and q[-1][0] >= a[i]:
                q.pop()
            pi = q[-1][1] if q else -1
            kst.update(qstart + len(q), Line(-a[i], -(-a[i] * pi)), 0, n - 1)
            if q:
                right[i] if it else left[i] = q[-1][1]
            q.append((a[i], i))
            if i >= k and q[0][1] == i - k:
                q.popleft()
                qstart += 1
            if q:
                v, idx = q[0]
                kst.update(qstart, Line(-v, -(-v * max(-1, i - k))), 0, n - 1)
            if i >= k - 1:
                kst.heaten_kst(i)
                ans.append(-kst.query(qstart, qstart + len(q) - 1, 0, n - 1))

        anss.append(ans)
        a.reverse()

    anss[1].reverse()
    right = [n - 1 - r for r in right]

    fans = anss[0]
    addevents = [[] for _ in range(len(fans))]
    remevents = [[] for _ in range(len(fans))]

    for i in range(n):
        len_range = right[i] - 1 - left[i]
        if len_range <= k:
            start = max(0, right[i] - 1 - (k - 1))
            addevents[start].append(len_range * a[i])
            ending = min(n - 1, left[i] + 1 + k - 1) - (k - 1)
            if ending + 1 < len(remevents):
                remevents[ending + 1].append(len_range * a[i])

    for i in range(len(fans)):
        fans[i] = max(fans[i], anss[1][i])

    ss = set()
    for i in range(len(addevents)):
        for v in addevents[i]:
            ss.add(v)
        for v in remevents[i]:
            ss.discard(v)
        if ss:
            fans[i] = max(fans[i], max(ss))

    print(" ".join(map(str, fans)))

if __name__ == "__main__":
    main()

