class Node:
    def __init__(self, b):
        self.nex = [None, None]
        self.bitnum = b

    def add(self, val):
        if self.bitnum == 0:
            return
        idx = (val >> (self.bitnum - 1)) & 1
        if self.nex[idx] is None:
            self.nex[idx] = Node(self.bitnum - 1)
        self.nex[idx].add(val)

    def closest(self, val):
        ans = 0
        if self.bitnum == 0:
            return ans
        idx = (val >> (self.bitnum - 1)) & 1
        if self.nex[idx] is not None:
            ans |= idx << (self.bitnum - 1)
            ans |= self.nex[idx].closest(val)
        elif self.nex[1 - idx] is not None:
            ans |= (1 - idx) << (self.bitnum - 1)
            ans |= self.nex[1 - idx].closest(val)
        return ans

def main():
