import sys
import heapq

def main():
    input = sys.stdin.read
    data = input().split()

    n = int(data[0])
    x = list(map(int, data[1:n+1]))
    ans = []
    possible = True
    pq = []

    for i in range(n):
        if x[i]:
            heapq.heappush(pq, (-x[i], i + 1))

    while pq:
        d, a = heapq.heappop(pq)
        d = -d
        if len(pq) < d:
            possible = False
            break
        reinsert = []
        for _ in range(d):
            d1, b = heapq.heappop(pq)
            d1 = -d1 - 1
            if d1:
                reinsert.append((-d1, b))
            ans.append((a, b))
        for p in reinsert:
            heapq.heappush(pq, p)

    if not possible:
        print("IMPOSSIBLE")
        return

    print(len(ans))
    for a, b in ans:
        print(a, b)

if __name__ == "__main__":
    main()

