def solve32(i, j):
    global t
    grid[i][j] = grid[i + 1][j] = grid[i][j + 1] = t
    t += 1
    grid[i + 2][j] = grid[i + 1][j + 1] = grid[i + 2][j + 1] = t
    t += 1

def solve23k(r1, r2, c1, c2):
    for i in range(r1, r2, 2):
        for j in range(c1, c2, 3):
            solve23(i, j)

def solve56(r1, r2, c1, c2):
    solve23k(r1 + 3, r2, c1, c2)
    i = r1
    for j in range(c1, c2, 2):
        solve32(i, j)

def solve59(r1, r2, c1, c2):
    global t
    solve32(r1, c1)
    solve32(r1, c1 + 2)
    solve23(r1 + 3, c1)
    solve23(r1, c1 + 4)
    solve32(r1, c1 + 7)
    grid[r1 + 3][c1 + 3] = grid[r1 + 4][c1 + 3] = grid[r1 + 4][c1 + 4] = t
    t += 1
    grid[r1 + 3][c1 + 3 + 2] = grid[r1 + 4][c1 + 3 + 2] = grid[r1 + 4][c1 + 4 + 2] = t
    t += 1
    grid[r1 - 1 + 3][c1 + 3 + 3] = grid[r1 - 1 + 4][c1 + 3 + 3] = grid[r1 - 1 + 4][c1 + 3 + 4] = t
    t += 1
    grid[r1 + 2][c1 + 4] = grid[r1 + 3][c1 + 4] = grid[r1 + 2][c1 + 5] = t
    t += 1
    grid[r1 + 4][c1 + 7] = grid[r1 + 4][c1 + 8] = grid[r1 + 3][c1 + 8] = t
    t += 1

def solve():
    global grid, t
    n, m = map(int, input().split())
    if (n * m) % 3 != 0 or min(n, m) <= 1:
        print("NO")
        return
    sw = False
    if m % 2 == 0 and n % 3 == 0:
        sw = not sw
        n, m = m, n
    if n % 2 == 0 and m % 3 == 0:
        grid = [[-1] * m for _ in range(n)]
        solve23k(0, n, 0, m)
        goto_end()
    if m % 3 != 0:
        n, m = m, n
        sw = not sw
    if m == 3 or n == 3:
        print("NO")
        return
    grid = [[-1] * m for _ in range(n)]
    for r in range(n, 5, -2):
        solve23k(r - 2, r, 0, m)
    c = m
    for c in range(m, 9, -6):
        solve56(0, 5, c - 6, c)
    if c == 6:
        solve56(0, 5, 0, 6)
    else:
        solve59(0, 5, 0, 9)

    goto_end()

def goto_end():
    print("YES")
    if sw:
        cp = [[0] * n for _ in range(m)]
        for i in range(n):
            for j in range(m):
                cp[j][i] = grid[i][j]
        n, m = m, n
        grid = cp

    adj = [[] for _ in range(t)]
    dirs = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    for i in range(n):
        for j in range(m):
            assert grid[i][j] != -1
    for i in range(n):
        for j in range(m):
            assert grid[i][j] != -1
            for dr, dc in dirs:
                i1, j1 = i + dr, j + dc
                if min(i1, j1) < 0 or i1 >= n or j1 >= m:
                    continue
                if grid[i1][j1] != grid[i][j]:
                    adj[grid[i][j]].append(grid[i1][j1])
    col = [-1] * t
    for i in range(t):
        adj[i].sort()
        adj[i] = list(dict.fromkeys(adj[i]))
        unused = 0
        seen = set()
        for j in adj[i]:
            if col[j] != -1:
                seen.add(col[j])
        for j in seen:
            if unused == j:
                unused += 1
        col[i] = unused
    for i in range(n):
        for j in range(m):
            grid[i][j] = col[grid[i][j]]
            print(chr(ord('A') + grid[i][j]), end="")
        print()

