def main():
    import sys
    from collections import defaultdict

    input = sys.stdin.read
    data = input().splitlines()

    n, m = map(int, data[0].split())
    if m % 2 == 1:
        print("IMPOSSIBLE")
        return

    visited = [0] * n
    deg = [0] * n
    edges = []
    adj = defaultdict(list)

    for i in range(1, m + 1):
        a, b = map(int, data[i].split())
        a -= 1
        b -= 1
        adj[a].append(b)
        adj[b].append(a)

    t = 1

    def dfs(cur, par):
        nonlocal t
        visited[cur] = t
        t += 1
        for nex in adj[cur]:
            if nex == par:
                continue
            if visited[nex] and visited[nex] < visited[cur]:
                edges.append((cur, nex))
                deg[cur] ^= 1
            elif not visited[nex]:
                dfs(nex, cur)
                if deg[nex]:
                    edges.append((nex, cur))
                    deg[nex] ^= 1
                else:
                    edges.append((cur, nex))
                    deg[cur] ^= 1

    works = True
    for i in range(n):
        if not visited[i]:
            dfs(i, -1)
        if deg[i]:
            works = False

    if works:
        for a, b in edges:
            print(a + 1, b + 1)
    else:
        print("IMPOSSIBLE")

