class UF:
    def __init__(self, n):
        self.e = [-1] * n
        self.ncomps = n

    def same_set(self, a, b):
        return self.find(a) == self.find(b)

    def size(self, x):
        return -self.e[self.find(x)]

    def find(self, x):
        if self.e[x] < 0:
            return x
        self.e[x] = self.find(self.e[x])
        return self.e[x]

    def join(self, a, b):
        a = self.find(a)
        b = self.find(b)
        if a == b:
            return False
        if self.e[a] > self.e[b]:
            a, b = b, a
        self.e[a] += self.e[b]
        self.e[b] = a
        self.ncomps -= 1
        return True


def main():
    import sys
    input = sys.stdin.read
    data = input().split()

    idx = 0
    n = int(data[idx])
    m = int(data[idx + 1])
    k = int(data[idx + 2])
    idx += 3

    uf = UF(n)
    edges = set()

    for _ in range(m):
        a = int(data[idx]) - 1
        b = int(data[idx + 1]) - 1
        idx += 2
        if a > b:
            a, b = b, a
        edges.add((a, b))

    qs = []
    for _ in range(k):
        f = int(data[idx]) - 1
        s = int(data[idx + 1]) - 1
        idx += 2
        if f > s:
            f, s = s, f
        qs.append((f, s))
        edges.discard((f, s))

    for a, b in edges:
        uf.join(a, b)

    ans = []
    for i in range(k - 1, -1, -1):
        ans.append(uf.ncomps)
        uf.join(qs[i][0], qs[i][1])

    print(" ".join(map(str, reversed(ans))))


