    for i in range(n):
        for j in range(n):
            to[i + j] += 1

    totdp = []
    for t in range(2):
        cands = []
        for i in range(len(to)):
            if (i & 1) == t:
                cands.append(to[i])
        cands.sort()

        cnt = []
        items = []
        for i in range(len(cands)):
            if i == 0 or cands[i] != cands[i - 1]:
                cnt.append(0)
                items.append(cands[i] - (0 if i == 0 else cands[i - 1]))
            cnt[-1] += 1

        cnt.reverse()
        items.reverse()
        for i in range(1, len(cnt)):
            cnt[i] += cnt[i - 1]

        dp = [[-1] * (n + 1) for _ in range(len(cnt) + 1)]

        def go(layer, used):
            if dp[layer + 1][used] != -1:
                return dp[layer + 1][used]
            if layer == -1:
                return 1 if used == 0 else 0

            ans = 0
            ans += go(layer - 1, used)

            if used >= 1 and items[layer] >= 1:
                free = cnt[layer] - (used - 1)
                if free >= 1:
                    ans += free * items[layer] * go(layer - 1, used - 1)
                    ans %= MOD

            if used >= 2 and items[layer] >= 2:
                free = cnt[layer] - (used - 2)
                if free >= 2:
                    ans += (free * (free - 1) % MOD) * go(layer - 1, used - 2)
                    ans %= MOD

            ans %= MOD
            return ans

        totdp.append([])
        for i in range(n + 1):
            v = go(len(cnt) - 1, i)
            totdp[-1].append(v)

    ans = 0
    for i in range(n + 1):
        for j in range(n + 1):
            if i < len(totdp[0]) and j < len(totdp[1]) and i + j == k:
                ans += (totdp[0][i] * totdp[1][j] % MOD)
                ans %= MOD

    print(ans)

