import sys
from collections import defaultdict

MOD = int(1e9 + 7)

class Comb:
    def __init__(self):
        self.inv = 1
        self.fact = 1
        self.inv_fact = 1

t = [Comb() for _ in range(2)]

def grow(n):
    while len(t) <= n:
        i = len(t)
        inv = MOD - (MOD // i) * t[MOD % i].inv % MOD
        t.append(Comb())
        t[i].inv = inv
        t[i].fact = i * t[i - 1].fact % MOD
        t[i].inv_fact = inv * t[i - 1].inv_fact % MOD

def C(n, k):
    if k < 0 or n < k:
        return 0
    grow(n)
    return t[n].fact * t[k].inv_fact % MOD * t[n - k].inv_fact % MOD

N = 501
dp = [[[-1] * N for _ in range(N)] for _ in range(N)]

def main():
    input = sys.stdin.read
    data = input().splitlines()

    n = int(data[0])
    grid = data[1:n + 1]

    ac = [-1] * n
    bc = [-1] * n

    for c in range(n):
        for r in range(n):
            if grid[r][c] == 'A':
                ac[c] = r
            if grid[r][c] == 'B':
                bc[c] = r

    cleared = 0
    astate = [0] * n
    bstate = [0] * n

    for i in range(n):
        if ac[i] != -1 and bc[i] != -1:
            astate[ac[i]] = bstate[bc[i]] = 2
        elif ac[i] != -1:
            astate[ac[i]] = 1
        elif bc[i] != -1:
            bstate[bc[i]] = 1
        else:
            cleared += 1

    cnt = [0] * 9
    for i in range(n):
        cnt[bstate[i] * 3 + astate[i]] += 1

    def go(ona, onb, offb):
        ans = dp[ona][onb][offb]
        if ans != -1:
            return ans
        if ona == 0 and onb == 0 and offb == 0:
            return 1
        assert onb >= 0 and ona >= 0 and offb >= 0
        res = 0
        if ona:
            if offb:
                res += offb * go(ona - 1, onb, offb - 1) % MOD
        else:
            if offb:
                res += offb * go(ona, onb, offb - 1) % MOD
            if onb:
                res += onb * go(ona, onb - 1, offb) % MOD
        return res % MOD

    ona = onb = matcheda = matchedb = offa = offb = 0
    for a in range(3):
        for b in range(3):
            if a == 2:
                matcheda += cnt[b * 3 + a]
            if a == 1:
                ona += cnt[b * 3 + a]
            if a == 0:
                offa += cnt[b * 3 + a]
            if b == 2:
                matchedb += cnt[b * 3 + a]
            if b == 1:
                onb += cnt[b * 3 + a]
            if b == 0:
                offb += cnt[b * 3 + a]

    ans = 0
    for i in range(1 + cnt[0]):
        for j in range(1 + cnt[3]):
            for k in range(1 + cnt[1]):
                offa -= i
                offb -= i
                offa -= j
                onb -= j
                ona -= k
                offb -= k
                ways = (C(cnt[0], i) * C(cnt[3], j) % MOD * C(cnt[1], k) % MOD)
                par = 1 if (i + j + k) % 2 == 0 else -1
                w = go(ona, onb, offb)
                ans = (ans + w * ways * par) % MOD
                offa += i
                offb += i
                offa += j
                onb += j
                ona += k
                offb += k

    grow(2 * n + 10)
    ans = (ans + MOD) % MOD
    ans = (ans * t[cleared].fact) % MOD
    print(ans)

if __name__ == "__main__":
    main()

