from collections import deque

def hsh(g):
    h = 0
    mult = 1
    for i in range(3):
        for j in range(3):
            h += mult * g[i][j]
            mult *= 9
    return h

pows = [1] * 15
for i in range(1, 15):
    pows[i] = 9 * pows[i - 1]

def swp(h, idx1, idx2):
    v1 = (h % pows[idx1 + 1]) // pows[idx1]
    v2 = (h % pows[idx2 + 1]) // pows[idx2]
    return h - pows[idx1] * v1 - pows[idx2] * v2 + pows[idx1] * v2 + pows[idx2] * v1

def unhash(h):
    res = [[0] * 3 for _ in range(3)]
    for i in range(3):
        for j in range(3):
            v = h % 9
            res[i][j] = v
            h //= 9
    return res

def main():
    grid = [list(map(int, input().split())) for _ in range(3)]
    for i in range(3):
        for j in range(3):
            grid[i][j] -= 1

    target = [[i * 3 + j for j in range(3)] for i in range(3)]
    targethash = hsh(target)

    seen = [False] * pows[9]
    q = deque()
    q.append((hsh(grid), 0))
    seen[hsh(grid)] = True
    ans = 0

    while q:
        h, v = q.popleft()
        if h == targethash:
            ans = v
            break

        for i in range(3):
            for j in range(2):
                h1 = swp(h, i * 3 + j, i * 3 + j + 1)
                if not seen[h1]:
                    q.append((h1, 1 + v))
                    seen[h1] = True

        for i in range(2):
            for j in range(3):
                h1 = swp(h, i * 3 + j, (i + 1) * 3 + j)
                if not seen[h1]:
                    q.append((h1, 1 + v))
                    seen[h1] = True

    print(ans)

if __name__ == "__main__":
    main()

