import sys
from collections import namedtuple

pi = namedtuple('pi', ['f', 's'])

inf = int(1e9)

class Node:
    def __init__(self, lo, hi):
        self.l = None
        self.r = None
        self.lo = lo
        self.hi = hi
        self.madd = 0
        self.info = pi(0, 0)
        if lo + 1 < hi:
            mid = lo + (hi - lo) // 2
            self.l = Node(lo, mid)
            self.r = Node(mid, hi)
            self.comb()

    def combine(self, l, r):
        return pi(min(l.f, r.f), max(l.s, r.s))

    def comb(self):
        self.info = self.combine(self.l.info, self.r.info)

    def query(self, L, R):
        if R <= self.lo or self.hi <= L:
            return pi(inf, -inf)
        if L <= self.lo and self.hi <= R:
            return self.info
        self.push()
        return self.combine(self.l.query(L, R), self.r.query(L, R))

    def add(self, L, R, x):
        if R <= self.lo or self.hi <= L:
            return
        if L <= self.lo and self.hi <= R:
            self.madd += x
            self.info = pi(self.info.f + x, self.info.s + x)
        else:
            self.push()
            self.l.add(L, R, x)
            self.r.add(L, R, x)
            self.comb()

    def push(self):
        if self.madd:
            self.l.add(self.lo, self.hi, self.madd)
            self.r.add(self.lo, self.hi, self.madd)
            self.madd = 0

def main():
    input = sys.stdin.read
    data = input().splitlines()
    n = int(data[0])
    tree = Node(0, n)
    results = []

    for i in range(n):
        v, t = map(int, data[i + 1].split())
        v -= 1
        tree.add(0, v + 1, -1 if t == 1 else 1)
        info = tree.query(0, n)
        if info.f >= 0:
            results.append("<")
        elif info.s <= 0:
            results.append(">")
        else:
            results.append("?")

    print("\n".join(results))

if __name__ == "__main__":
    main()

