class FT:
    def __init__(self, n):
        self.s = [0] * n
        self.n = n

    def update(self, pos, dif):
        while pos < len(self.s):
            self.s[pos] += dif
            pos |= pos + 1

    def query(self, pos):
        res = 0
        while pos > 0:
            res += self.s[pos - 1]
            pos &= pos - 1
        return res

    def lower_bound(self, sum):
        if sum <= 0:
            return -1
        pos = 0
        for pw in (1 << 25, 1 << 24, 1 << 23, 1 << 22, 1 << 21, 1 << 20, 1 << 19, 1 << 18, 1 << 17, 1 << 16, 1 << 15, 1 << 14, 1 << 13, 1 << 12, 1 << 11, 1 << 10, 1 << 9, 1 << 8, 1 << 7, 1 << 6, 1 << 5, 1 << 4, 1 << 3, 1 << 2, 1 << 1, 1):
            if pos + pw <= len(self.s) and self.s[pos + pw - 1] < sum:
                pos += pw
                sum -= self.s[pos - 1]
        return pos

