    for a in range(n, 0, -1):
        ls[a] = sorted(set(ls[a]))
        while ls[a] and ls[a][-1] == a:
            ls[a].pop()
        for b in ls[a]:
            if b == 0:
                continue
            c = a % (b + 1)
            ls[max(c, b)].append(min(c, b))

    ans = defaultdict(list)
